import React, { useState } from 'react'
import ReactDOM from 'react-dom'

//  'Header'
const Header = (props) => {
    console.log(props)
    return (
        <h1>{props.course}</h1>
    )
}

//  'Part'
const Part = (props) => {
    console.log(props)
    return (
        <p>{props.name} {props.exercises}</p>
    )
}

//  'Content'
const Content = (props) => {
    console.log(props)
    return (
        <div>
            <Part name = {props.parts[0].name}
                  exercises = {props.parts[0].exercises}
            />
            <Part name = {props.parts[1].name}
                  exercises = {props.parts[1].exercises}
            />
            <Part name = {props.parts[2].name}
                  exercises = {props.parts[2].exercises}
            />
        </div>
    )
}

//  'Total'
const Total = (props) => {
    console.log(props)
    return (
        <div>
            <p>Number of exercises {
                props.total[0].exercises +
                props.total[1].exercises +
                props.total[2].exercises}
            </p>
        </div>
    )
}

const Statistics = (props) => {
    const bad = props.bad
    const good = props.good
    const neutral = props.neutral
    const all = bad + good + neutral
    const avrg = good - bad
    return (
        <div>
        <h2> statistics </h2>
        <tbody>
        <tr>
            <td>good:</td>
            <td>{good}</td>
        </tr>
        <tr>
            <td>neutral:</td>
            <td>{neutral}</td>
        </tr>
        <tr>
            <td>bad:</td>
            <td>{bad}</td>
        </tr>
        <tr>
            <td>average:</td>
            <td>{avrg / all}</td>
        </tr>
        <tr>
            <td>positive:</td>
            <td>{good / all}%</td>
        </tr>
        </tbody>
        </div>
    )
}

// 'App'
const App = (props) => {
    const [good, setGood] = useState(0)
    const [neutral, setNeutral] = useState(0)
    const [bad, setBad] = useState(0)

    const course = {
        name: 'Half Stack application development',
        parts:  [
            {
            name:       'Fundamentals of React',
            exercises:  10
            },
            {
            name:       'Using props to pass data',
            exercises:  7
            },
            {
            name: 'State of component',
            exercises:  14
            }
        ]
    }

    const addGood = () => {
        setGood(good + 1)
    }

    const addNeutral = () => {
        setNeutral(neutral + 1)
    }

    const addBad = () => {
        setBad(bad + 1)
    }

    function Feedback() {
        if (good > 0 || bad > 0 || neutral > 0) {
          return <Statistics good={good} neutral={neutral} bad={bad} />

        }
        return <h4>No feedback given</h4>
    }

    return (
        <div>
            <div>
            <h1>
            Give feedback
            </h1>
            <button type="button" onClick={addGood}>good</button>
            <button type="button" onClick={addNeutral}>neutral</button>
            <button type="button" onClick={addBad}>bad</button>
            <Feedback />
            </div>
            <Header course={course.name} />
            <Content parts = {course.parts} />
            <Total total = {course.parts} />
        </div>
    )
}




ReactDOM.render(<App />, document.getElementById('root'))
