import React, { useState } from 'react'
import ReactDOM from 'react-dom'

//  'Header'
const Header = (props) => {
    console.log(props)
    return (
        <h1>{props.course}</h1>
    )
}

//  'Part'
const Part = (props) => {
    console.log(props)
    return (
        <p>{props.name} {props.exercises}</p>
    )
}

//  'Content'
const Content = (props) => {
    console.log(props)
    return (
        <div>
            <Part name = {props.parts[0].name}
                  exercises = {props.parts[0].exercises}
            />
            <Part name = {props.parts[1].name}
                  exercises = {props.parts[1].exercises}
            />
            <Part name = {props.parts[2].name}
                  exercises = {props.parts[2].exercises}
            />
        </div>
    )
}

//  'Total'
const Total = (props) => {
    console.log(props)
    return (
        <div>
            <p>Number of exercises {
                props.total[0].exercises +
                props.total[1].exercises +
                props.total[2].exercises}
            </p>
        </div>
    )
}


const Hello = ({name, age}) => {
  const bornYear = () => {
      const yearNow = new Date().getFullYear()
      return yearNow - age
  }
  return (
    <div>
      <p>
        Hello {name}, you are {age} years old
      </p>
      <p>So you probably born {bornYear()}</p>
    </div>
  )
}

// 'App'
const App = (props) => {
    const [counter, setCounter] = useState(0)
    const [clicks, setClicks] = useState({
        left: 0, right: 0
    })
    const [allClicks, setAll] = useState([])

    const handleLeftClick = () => {
      setClicks({...clicks, left: clicks.left + 1})
      setAll(allClicks.concat('L'))
    }

    const handleRightClick = () => {
      setClicks({...clicks, right: clicks.right + 1})
      setAll(allClicks.concat('R'))
    }

    const name = 'Peter'
    const age = 10

    const course = {
        name: 'Half Stack application development',
        parts:  [
            {
            name:       'Fundamentals of React',
            exercises:  10
            },
            {
            name:       'Using props to pass data',
            exercises:  7
            },
            {
            name: 'State of component',
            exercises:  14
            }
        ]
    }

    return (
        <div>
            <div>
              {clicks.left}
              <button onClick={handleLeftClick}>
                left
              </button>
              {clicks.right }
              <button onClick={handleRightClick}>
                right
              </button>
              <p>
                  {allClicks.join(' ')}
              </p>
            </div>


            <div>
            <button type="button" onClick={() => setCounter(counter + 1)}>+</button>
            <button type="button" onClick={() => setCounter(0)}>0</button>
            <div> {counter} </div>
              <h1>
                Greetings
              </h1>
              <Hello name='Maya' age={26 + 10} />
              <Hello name={name} age={age} />
            </div>
            <Header course={course.name} />
            <Content parts = {course.parts} />
            <Total total = {course.parts} />
        </div>
    )
}




ReactDOM.render(<App />, document.getElementById('root'))
